const fs = require('fs');
const path = require('path');

const src = './src';
const dist = './dist';
const extensions = ['scss', 'css'];

fs.readdir(src, (err, files) => {
	files.forEach(file => {
		var fileExtension = file.split('.').slice(-1)[0];
		if (extensions.indexOf(fileExtension) === -1) { return; }

		fs.createReadStream(path.join(src, file))
			.pipe(fs.createWriteStream(path.join(dist, src, file)));
	});
})
